import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Header.css';

const Header = ({ className = '' }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [activeDropdown, setActiveDropdown] = useState(null);
  
  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  const toggleDropdown = (dropdownName) => {
    if (activeDropdown === dropdownName) {
      setActiveDropdown(null);
    } else {
      setActiveDropdown(dropdownName);
    }
  };

  const closeDropdown = () => {
    setActiveDropdown(null);
  };

  // Dropdown menu data
  const dropdownMenus = {
    'plants-fish': {
      title: 'Plants & Fish',
      items: [
        { name: 'Aquatic Plants', path: '/plants-fish/aquatic-plants' },
        { name: 'Koi & Goldfish', path: '/plants-fish/koi-goldfish' },
        { name: 'Tropical Fish', path: '/plants-fish/tropical-fish' },
        { name: 'Water Lilies', path: '/plants-fish/water-lilies' },
        { name: 'Floating Plants', path: '/plants-fish/floating-plants' },
        { name: 'Marginal Plants', path: '/plants-fish/marginal-plants' }
      ]
    },
    'pond-services': {
      title: 'Pond Services',
      items: [
        { name: 'Pond Design & Build', path: '/pond-services/design-build' },
        { name: 'Pond Maintenance', path: '/pond-services/maintenance' },
        { name: 'Water Quality Testing', path: '/pond-services/water-quality' },
        { name: 'Pond Cleaning', path: '/pond-services/cleaning' },
        { name: 'Equipment Repair', path: '/pond-services/repair' },
        { name: 'Seasonal Services', path: '/pond-services/seasonal' }
      ]
    },
    'pond-supplies': {
      title: 'Pond Supplies',
      items: [
        { name: 'Filtration Systems', path: '/pond-supplies/filtration' },
        { name: 'Pumps & Aeration', path: '/pond-supplies/pumps-aeration' },
        { name: 'Pond Liners', path: '/pond-supplies/liners' },
        { name: 'Water Treatments', path: '/pond-supplies/water-treatments' },
        { name: 'Pond Lighting', path: '/pond-supplies/lighting' },
        { name: 'Tools & Accessories', path: '/pond-supplies/tools' }
      ]
    },
    'resources': {
      title: 'Resources',
      items: [
        { name: 'Care Guides', path: '/resources/care-guides' },
        { name: 'Pond Tips & Tricks', path: '/resources/tips-tricks' },
        { name: 'Seasonal Care', path: '/resources/seasonal-care' },
        { name: 'Troubleshooting', path: '/resources/troubleshooting' },
        { name: 'Video Tutorials', path: '/resources/video-tutorials' },
        { name: 'FAQ', path: '/resources/faq' }
      ]
    },
    'pond-gallery': {
      title: 'Pond Gallery',
      items: [
        { name: 'Residential Ponds', path: '/pond-gallery/residential' },
        { name: 'Commercial Projects', path: '/pond-gallery/commercial' },
        { name: 'Koi Ponds', path: '/pond-gallery/koi-ponds' },
        { name: 'Water Features', path: '/pond-gallery/water-features' },
        { name: 'Before & After', path: '/pond-gallery/before-after' },
        { name: 'Customer Projects', path: '/pond-gallery/customer-projects' }
      ]
    }
  };

  return (
    <header className={`header ${className}`}>
      {/* Top Section - Logo and Company Name */}
      <div className="header-top">
        <div className="header-container">
          <div className="logo-section">
            <Link to="/" className="logo-link">
              <div className="company-name">Utah Water Gardens</div>
              <div className="lotus-logo">
                <svg width="40" height="40" viewBox="0 0 40 40" fill="none">
                  {/* Lotus Flower Petals */}
                  <path 
                    d="M20 8C22 6 24 8 26 10C28 12 26 16 24 18C22 20 20 22 18 20C16 18 14 14 16 12C18 10 20 8 20 8Z" 
                    fill="#ff4444"
                  />
                  <path 
                    d="M20 6C22 4 26 6 28 8C30 10 28 16 26 18C24 20 20 22 18 20C16 18 18 12 20 10C22 8 20 6 20 6Z" 
                    fill="#ff6666"
                  />
                  <path 
                    d="M20 4C24 2 28 4 30 6C32 8 30 16 28 18C26 20 22 22 20 20C18 18 20 10 22 8C24 6 20 4 20 4Z" 
                    fill="#ff8888"
                  />
                  {/* Water Base */}
                  <path 
                    d="M8 32C12 28 16 30 20 32C24 30 28 28 32 32C30 34 26 36 20 36C14 36 10 34 8 32Z" 
                    fill="#87ceeb"
                  />
                  <path 
                    d="M10 30C14 26 18 28 20 30C22 28 26 26 30 30C28 32 24 34 20 34C16 34 12 32 10 30Z" 
                    fill="#b0e0e6"
                  />
                </svg>
              </div>
            </Link>
          </div>
        </div>
      </div>

      {/* Bottom Section - Navigation and Contact Button */}
      <div className="header-bottom">
        <div className="header-container">
          <nav className={`nav ${isMenuOpen ? 'nav-open' : ''}`}>
            <ul className="nav-list">
              {Object.entries(dropdownMenus).map(([key, menu]) => (
                <li 
                  key={key}
                  className="nav-item"
                  onMouseEnter={() => setActiveDropdown(key)}
                  onMouseLeave={() => setActiveDropdown(null)}
                >
                  <button 
                    className={`nav-link ${activeDropdown === key ? 'active' : ''}`}
                    onClick={() => toggleDropdown(key)}
                  >
                    {menu.title}
                    <span className="dropdown-arrow">▼</span>
                  </button>
                  
                  {/* Dropdown Menu */}
                  <div className={`dropdown-menu ${activeDropdown === key ? 'show' : ''}`}>
                    <div className="dropdown-content">
                      <div className="dropdown-header">
                        <h3>{menu.title}</h3>
                      </div>
                      <ul className="dropdown-list">
                        {menu.items.map((item, index) => (
                          <li key={index}>
                            <Link 
                              to={item.path} 
                              onClick={() => {
                                closeMenu();
                                closeDropdown();
                              }}
                              className="dropdown-link"
                            >
                              {item.name}
                            </Link>
                          </li>
                        ))}
                      </ul>
                    </div>
                  </div>
                </li>
              ))}
            </ul>
          </nav>
          
          <div className="contact-button-container">
            <Link to="/contact" className="contact-button" onClick={closeMenu}>
              Contact
            </Link>
          </div>

          {/* Mobile Menu Toggle */}
          <div className="mobile-menu-toggle" onClick={toggleMenu}>
            <span></span>
            <span></span>
            <span></span>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
